import React, { useState } from "react";
import { searchBookings } from "../services/bookingService.js";
import Button from "./Button"; // Import your Button component

const Search = ({ onResults }) => {
  const [location, setLocation] = useState("");
  const [checkIn, setCheckIn] = useState("");
  const [checkOut, setCheckOut] = useState("");
  const [roomType, setRoomType] = useState("");

  const handleSearch = async (e) => {
    e.preventDefault();

    const criteria = {
      location,
      checkIn,
      checkOut,
      roomType,
    };

    try {
      const results = await searchBookings(criteria);
      onResults(results);
    } catch (error) {
      console.error("Search failed", error);
    }
  };

  return (
    <div className='max-w-4xl mx-auto p-6 bg-white shadow-lg rounded-lg mt-6'>
      <h2 className='text-3xl font-semibold text-gray-800 mb-6 text-center'>
        Find Your Perfect Stay
      </h2>
      <form onSubmit={handleSearch} className='space-y-6'>
        {/* Location Input */}
        <div className='flex flex-col md:flex-row md:space-x-4'>
          <label
            className='text-gray-700 font-medium mb-2 md:w-1/4'
            htmlFor='location'>
            Location:
          </label>
          <input
            id='location'
            type='text'
            value={location}
            onChange={(e) => setLocation(e.target.value)}
            className='flex-1 px-4 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500'
            placeholder='Enter city or hotel'
          />
        </div>

        {/* Check-In Input */}
        <div className='flex flex-col md:flex-row md:space-x-4'>
          <label
            className='text-gray-700 font-medium mb-2 md:w-1/4'
            htmlFor='checkIn'>
            Check-In Date:
          </label>
          <input
            id='checkIn'
            type='date'
            value={checkIn}
            onChange={(e) => setCheckIn(e.target.value)}
            className='flex-1 px-4 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500'
          />
        </div>

        {/* Check-Out Input */}
        <div className='flex flex-col md:flex-row md:space-x-4'>
          <label
            className='text-gray-700 font-medium mb-2 md:w-1/4'
            htmlFor='checkOut'>
            Check-Out Date:
          </label>
          <input
            id='checkOut'
            type='date'
            value={checkOut}
            onChange={(e) => setCheckOut(e.target.value)}
            className='flex-1 px-4 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500'
          />
        </div>

        {/* Room Type Select */}
        <div className='flex flex-col md:flex-row md:space-x-4'>
          <label
            className='text-gray-700 font-medium mb-2 md:w-1/4'
            htmlFor='roomType'>
            Room Type:
          </label>
          <select
            id='roomType'
            value={roomType}
            onChange={(e) => setRoomType(e.target.value)}
            className='flex-1 px-4 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500'>
            <option value=''>Select a type</option>
            <option value='single'>Single</option>
            <option value='double'>Double</option>
            <option value='suite'>Suite</option>
          </select>
        </div>

        {/* Submit Button */}
        <div className='text-center'>
          <Button
            type='search'
            onClick={handleSearch} // Pass the handleSearch function to Button
            className='w-full md:w-auto' // Ensure the button is full-width on small screens and auto-width on larger screens
          />
        </div>
      </form>
    </div>
  );
};

export default Search;
