import React, { useState } from "react";
import { Link } from "react-router-dom";

const Header = () => {
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };

  return (
    <header className='bg-gray-800 text-white px-4 py-3 flex justify-between items-center'>
      <div className='text-2xl font-bold'>
        <Link to='/'>HotelBooking</Link>
      </div>

      {/* Hamburger Icon for Mobile */}
      <div className='md:hidden cursor-pointer' onClick={toggleMobileMenu}>
        <span className='block w-6 h-0.5 bg-white mb-1'></span>
        <span className='block w-6 h-0.5 bg-white mb-1'></span>
        <span className='block w-6 h-0.5 bg-white'></span>
      </div>

      {/* Navigation Links */}
      <nav
        className={`md:flex space-x-6 text-lg ${
          isMobileMenuOpen ? "flex" : "hidden"
        } flex-col md:flex-row absolute md:static top-16 left-0 w-full md:w-auto bg-gray-800 md:bg-transparent p-4 md:p-0`}>
        <Link
          to='/'
          className='hover:text-gray-400'
          onClick={() => setIsMobileMenuOpen(false)}>
          Home
        </Link>
        <Link
          to='/booking'
          className='hover:text-gray-400'
          onClick={() => setIsMobileMenuOpen(false)}>
          Book a Room
        </Link>
        <Link
          to='/about'
          className='hover:text-gray-400'
          onClick={() => setIsMobileMenuOpen(false)}>
          About Us
        </Link>
        <Link
          to='/contact'
          className='hover:text-gray-400'
          onClick={() => setIsMobileMenuOpen(false)}>
          Contact
        </Link>
      </nav>
    </header>
  );
};

export default Header;
